                        } else
                        {
                            $('#email_error_message').html(data.errmessage);
                            $('#email_error_message').slideDown();
                        }
                        scroll_to_div('contact_form');
                        hide_comm_mask();
                    }
                });
                return false;
            }
            function open_profile_chat(id) {
                window.open('https://www.artoflivingmatrimony.org/search/view-profile/' + id);
            }
        </script>
    </body>

</html>